import React from 'react';
import './App.css';

class Form extends React.Component {
  state = {
    selectTecnlogy: '',
    textName: '',
    textEmail: '',
    textArea: '',
  }

  handleChange = ({ target }) => {
    console.log(target)
    const { name, value } = target
    this.setState({
      [name]: value
    })
  }

  render() {
    const { selectTecnlogy, textName, textEmail, textArea } = this.state;
    return (
      <div>
        <form className='formField'>
        <h1> Form </h1>
        <label htmlFor='select'> 
        Select the Technology:
        </label>
          <select id='select' name='selectTecnlogy' 
          value={ selectTecnlogy } 
          onChange={ this.handleChange }>
            <option> JavaScript </option>
            <option>React</option>
            <option>Golang</option>
            <option>Python</option>
        </select>

        <label htmlFor='input-text'>
          Name:
        <input 
          id='input-text'
          name="textName"
          type="text" 
          // value={ textName } 
          onChange={ this.handleChange }/>
        </label>

        <label htmlFor="email">
            E-mail:
        <input
          id="email"
          name="textEmail"
          type="email" 
          value={ textEmail } 
          onChange={ this.handleChange }
            />
          </label>

        <label htmlFor="area-text">
          Text Area:
        <textarea id="area-text" 
          name="textArea" 
          value={ textArea } 
          onChange={ this.handleChange } />
        </label>
          </form>
      </div>
    )
  }
}

export default Form;
