import React from 'react';
import './App.css';

class Form extends React.Component {
  state = {
    selectTecnlogy: '',
    textName: '',
    textEmail: '',
    textArea: '',
  }

  inputNameOption = (event) => {
    this.setState({ textName: event.target.value })
  }

  inputEmailOption = (event) => {
    this.setState({ textEmail: event.target.value })
  }

  textAreaText = (event) => {
    this.setState({ textArea: event.target.value })
  }

  selectionOption  = (event) => {
    this.setState({ selectTecnlogy: event.target.value })
  }


  render() {
    return (
      <div>
        <form className='formField'>
        <h1> Form </h1>
        <label htmlFor='select'> 
        Select the Technology:
        </label>
        <select id='select'>
          <option value={ this.setState.selectTecnlogy} onChange={ this.selectionOption }> JavaScript </option>
          <option value={ this.setState.selectTecnlogy} onChange={ this.selectionOption }>React</option>
          <option value="golang">Golang</option>
          <option value="python">Python</option>
        </select>

        <label htmlFor='input-text'>
          Name:
        <input 
         id='input-text'
          name="textName"
          type="text" value={ this.setState.textName} onChange={ this.inputNameOption }/>
        </label>

        <label htmlFor="email">
            E-mail:
            <input
              id="email"
              name="email"
              type="email" value={ this.setState.textEmail} onChange={ this.inputEmailOption }
            />
          </label>

          <label htmlFor="area-text">
            Text Area:
            <textarea id="area-text" name="area-text" value={ this.setState.textArea} onChange={ this.textAreaText } />
          </label>
          </form>
      </div>
    )
  }
}

export default Form;
